import React from 'react';
import { motion } from 'framer-motion';

const Features = () => {
  const services = [
    {
      icon: "ðŸ’»",
      title: "Web Development",
      description: "Creating responsive and modern web applications using latest technologies like React, Node.js, and modern frameworks.",
      color: "from-cyan-400 to-blue-500"
    },
    {
      icon: "ðŸŽ¨",
      title: "Graphic Design",
      description: "Designing beautiful and functional user interfaces with focus on user experience and modern design principles.",
      color: "from-blue-400 to-purple-500"
    },
    {
      icon: "ðŸ“±",
      title: "Digital Marketing",
      description: "Building engaging content strategies and growing online presence through YouTube, social media, and digital platforms.",
      color: "from-purple-400 to-pink-500"
    }
  ];

  return (
    <section className="py-20 px-6 relative overflow-hidden">
      {/* Background Elements */}
      <div className="absolute inset-0">
        <div className="absolute top-20 right-20 w-96 h-96 bg-cyan-500/5 rounded-full blur-3xl"></div>
        <div className="absolute bottom-20 left-20 w-80 h-80 bg-blue-500/5 rounded-full blur-3xl"></div>
      </div>

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Section Header */}
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
          viewport={{ once: true }}
        >
          <h2 className="text-4xl md:text-5xl font-bold text-white mb-4">
            Our <span className="text-cyan-400">Services</span>
          </h2>
          <p className="text-gray-300 text-lg max-w-2xl mx-auto">
            Comprehensive digital solutions tailored to meet your business needs and drive growth
          </p>
        </motion.div>

        {/* Services Grid */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <motion.div
              key={service.title}
              className="gaming-card p-8 text-center group cursor-pointer"
              style={{
                background: 'linear-gradient(135deg, rgba(10, 14, 26, 0.95), rgba(26, 31, 58, 0.9), rgba(42, 47, 74, 0.95))',
                border: '2px solid rgba(0, 255, 255, 0.4)',
                boxShadow: '0 8px 32px rgba(0, 0, 0, 0.7), 0 0 20px rgba(0, 255, 255, 0.15)'
              }}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2, duration: 0.8 }}
              viewport={{ once: true }}
              whileHover={{ 
                scale: 1.05,
                boxShadow: "0 20px 60px rgba(0, 0, 0, 0.8), 0 0 40px rgba(0, 255, 255, 0.3), 0 0 80px rgba(0, 255, 255, 0.15)"
              }}
            >
              {/* Icon */}
              <motion.div
                className="text-6xl mb-6"
                whileHover={{ scale: 1.2, rotate: 10 }}
                transition={{ duration: 0.3 }}
              >
                {service.icon}
              </motion.div>

              {/* Title */}
              <h3 className="text-2xl font-bold text-white mb-4 group-hover:text-cyan-400 transition-colors duration-300">
                {service.title}
              </h3>

              {/* Description */}
              <p className="text-gray-300 leading-relaxed mb-6">
                {service.description}
              </p>

              {/* Learn More Button */}
              <motion.button
                className="w-full py-3 rounded-lg font-semibold transition-all duration-300"
                style={{
                  background: 'linear-gradient(135deg, rgba(0, 255, 255, 0.2), rgba(0, 153, 255, 0.2))',
                  border: '2px solid rgba(0, 255, 255, 0.4)',
                  color: '#00ffff'
                }}
                whileHover={{ 
                  scale: 1.02,
                  background: 'linear-gradient(135deg, #00ffff, #0099ff)',
                  color: '#0f172a'
                }}
                whileTap={{ scale: 0.98 }}
              >
                Learn More
              </motion.button>

              {/* Hover Glow Effect */}
              <motion.div
                className={`absolute inset-0 bg-gradient-to-r ${service.color} opacity-0 group-hover:opacity-5 transition-opacity duration-500 rounded-2xl pointer-events-none`}
              />
            </motion.div>
          ))}
        </div>

        {/* Bottom CTA */}
        <motion.div
          className="text-center mt-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.8, duration: 0.8 }}
          viewport={{ once: true }}
        >
          <motion.button
            className="gaming-btn font-semibold px-10 py-4"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
          >
            View All Services
          </motion.button>
        </motion.div>
      </div>
    </section>
  );
};

export default Features;
