import React from 'react';
import { motion } from 'framer-motion';

const Features = () => {
  const features = [
    {
      icon: 'üéØ',
      title: 'Quality Assurance Excellence',
      description: 'Advanced testing methodologies with automated frameworks and comprehensive bug tracking systems',
      tech: ['Selenium', 'Cypress', 'Jest', 'TestRail'],
      color: '#00D4FF',
      stats: '500+ Bugs Fixed'
    },
    {
      icon: '‚ö°',
      title: 'Frontend Development',
      description: 'Modern React applications with responsive design and optimized performance metrics',
      tech: ['React', 'TypeScript', 'Tailwind', 'Next.js'],
      color: '#39FF14',
      stats: '15+ Projects'
    },
    {
      icon: 'üîÆ',
      title: 'API Integration & Testing',
      description: 'RESTful API development and comprehensive endpoint testing with automated validation',
      tech: ['Postman', 'REST API', 'GraphQL', 'Node.js'],
      color: '#FF1493',
      stats: '100+ APIs Tested'
    },
    {
      icon: 'üöÄ',
      title: 'DevOps & Deployment',
      description: 'CI/CD pipeline implementation with automated testing and deployment strategies',
      tech: ['Git', 'Docker', 'Jenkins', 'AWS'],
      color: '#FF6B35',
      stats: '99.9% Uptime'
    },
    {
      icon: 'üõ°Ô∏è',
      title: 'Security Testing',
      description: 'Comprehensive security audits and vulnerability assessments for web applications',
      tech: ['OWASP', 'Burp Suite', 'Security Audit', 'Penetration Testing'],
      color: '#8A2BE2',
      stats: '0 Security Breaches'
    },
    {
      icon: 'üìä',
      title: 'Performance Analytics',
      description: 'Advanced performance monitoring and optimization with real-time analytics dashboards',
      tech: ['Google Analytics', 'Lighthouse', 'WebVitals', 'Performance Testing'],
      color: '#00FFFF',
      stats: '85% Performance Boost'
    }
  ];

  return (
    <section className="py-32 px-4 relative overflow-hidden">
      {/* Professional Background Effects */}
      <div className="absolute inset-0">
        {[...Array(50)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute rounded-full opacity-10"
            style={{
              width: `${Math.random() * 4 + 2}px`,
              height: `${Math.random() * 4 + 2}px`,
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              backgroundColor: '#00D4FF'
            }}
            animate={{
              y: [-10, 10],
              opacity: [0.1, 0.3, 0.1],
              scale: [1, 1.2, 1],
            }}
            transition={{
              duration: 3 + Math.random() * 2,
              repeat: Infinity,
              delay: Math.random() * 2,
            }}
          />
        ))}
      </div>

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Enhanced Header */}
        <motion.div
          className="text-center mb-20"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
          viewport={{ once: true }}
        >
          <motion.h2 
            className="text-6xl md:text-8xl font-black mb-6"
            initial={{ scale: 0.5, rotateY: -180 }}
            whileInView={{ scale: 1, rotateY: 0 }}
            transition={{ duration: 1.5, type: "spring", stiffness: 100 }}
            viewport={{ once: true }}
          >
            <span style={{
              background: 'linear-gradient(45deg, #00D4FF, #39FF14, #FF1493, #FF6B35)',
              backgroundSize: '400% 400%',
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              textShadow: '0 0 30px rgba(0, 212, 255, 0.5)',
              animation: 'cyber-scan 3s ease-in-out infinite'
            }}>
              Professional Expertise
            </span>
          </motion.h2>
          <motion.p 
            className="text-xl max-w-4xl mx-auto leading-relaxed"
            style={{ color: '#E0E6ED' }}
            initial={{ opacity: 0 }}
            whileInView={{ opacity: 1 }}
            transition={{ delay: 0.5, duration: 1 }}
            viewport={{ once: true }}
          >
            Comprehensive technical skills and proven expertise in software quality assurance, 
            frontend development, and modern web technologies with industry-standard practices.
          </motion.p>
        </motion.div>

        {/* Enhanced Features Grid */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8 mb-16">
          {features.map((feature, index) => (
            <motion.div
              key={index}
              className="gaming-card p-8 rounded-2xl backdrop-blur-xl transform-gpu relative overflow-hidden"
              initial={{ opacity: 0, y: 100, rotateX: -30 }}
              whileInView={{ opacity: 1, y: 0, rotateX: 0 }}
              transition={{ 
                delay: index * 0.15, 
                duration: 0.8,
                type: "spring",
                stiffness: 100
              }}
              viewport={{ once: true }}
              whileHover={{
                scale: 1.05,
                rotateY: 8,
                boxShadow: `0 30px 60px rgba(${feature.color === '#00D4FF' ? '0, 212, 255' : 
                  feature.color === '#39FF14' ? '57, 255, 20' :
                  feature.color === '#FF1493' ? '255, 20, 147' :
                  feature.color === '#FF6B35' ? '255, 107, 53' :
                  feature.color === '#8A2BE2' ? '138, 43, 226' : '0, 255, 255'}, 0.4)`
              }}
            >
              {/* Animated Border */}
              <motion.div
                className="absolute inset-0 rounded-2xl"
                style={{
                  background: `linear-gradient(45deg, ${feature.color}40, transparent, ${feature.color}40)`,
                  backgroundSize: '400% 400%'
                }}
                animate={{
                  backgroundPosition: ['0% 0%', '100% 100%', '0% 0%']
                }}
                transition={{
                  duration: 3,
                  repeat: Infinity,
                  ease: "linear"
                }}
              />

              {/* Content */}
              <div className="relative z-10">
                <motion.div
                  className="flex items-center justify-between mb-6"
                  whileHover={{ scale: 1.1 }}
                  transition={{ duration: 0.3 }}
                >
                  <span className="text-5xl">{feature.icon}</span>
                  <motion.div 
                    className="text-sm font-bold px-3 py-1 rounded-full"
                    style={{
                      background: `${feature.color}20`,
                      border: `1px solid ${feature.color}`,
                      color: feature.color
                    }}
                    whileHover={{ scale: 1.1, rotate: 5 }}
                  >
                    {feature.stats}
                  </motion.div>
                </motion.div>

                <h3 
                  className="text-2xl font-bold mb-4"
                  style={{ color: feature.color }}
                >
                  {feature.title}
                </h3>

                <p className="mb-6 leading-relaxed text-base" style={{ color: '#E0E6ED' }}>
                  {feature.description}
                </p>

                <div className="flex flex-wrap gap-2">
                  {feature.tech.map((tech, techIndex) => (
                    <motion.span
                      key={techIndex}
                      className="px-3 py-1 text-sm rounded-full font-medium"
                      style={{
                        background: `${feature.color}15`,
                        border: `1px solid ${feature.color}40`,
                        color: feature.color
                      }}
                      whileHover={{ 
                        scale: 1.1,
                        backgroundColor: `${feature.color}30`,
                        y: -2
                      }}
                      transition={{ duration: 0.2 }}
                    >
                      {tech}
                    </motion.span>
                  ))}
                </div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Professional Achievement Stats */}
        <motion.div
          className="grid md:grid-cols-4 gap-8"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
          viewport={{ once: true }}
        >
          {[
            { number: '2+', label: 'Years Experience', color: '#00D4FF', icon: 'üìÖ' },
            { number: '500+', label: 'Bugs Resolved', color: '#39FF14', icon: 'üêõ' },
            { number: '15+', label: 'Projects Completed', color: '#FF1493', icon: 'üöÄ' },
            { number: '99.9%', label: 'Client Satisfaction', color: '#FF6B35', icon: '‚≠ê' }
          ].map((stat, index) => (
            <motion.div
              key={index}
              className="gaming-card p-6 rounded-xl text-center backdrop-blur-xl relative overflow-hidden"
              whileHover={{ 
                scale: 1.1,
                rotateY: 10,
                boxShadow: `0 20px 40px rgba(${stat.color === '#00D4FF' ? '0, 212, 255' : 
                  stat.color === '#39FF14' ? '57, 255, 20' :
                  stat.color === '#FF1493' ? '255, 20, 147' : '255, 107, 53'}, 0.4)`
              }}
              initial={{ scale: 0, rotate: -180 }}
              whileInView={{ scale: 1, rotate: 0 }}
              transition={{ delay: index * 0.1, duration: 0.6, type: "spring" }}
              viewport={{ once: true }}
            >
              <motion.div 
                className="text-3xl mb-2"
                whileHover={{ scale: 1.3, rotate: 15 }}
              >
                {stat.icon}
              </motion.div>
              <div 
                className="text-3xl font-black mb-2"
                style={{ color: stat.color }}
              >
                {stat.number}
              </div>
              <div className="text-sm uppercase tracking-wider font-medium" style={{ color: '#E0E6ED' }}>
                {stat.label}
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Features;
