import React from 'react';
import { motion } from 'framer-motion';

const Projects = () => {
  const projects = [
    {
      title: "Netflix Clone",
      description: "Built UI clone using HTML, CSS, JS with responsive design and smooth animations.",
      problem: "Create a pixel-perfect Netflix interface with responsive design",
      solution: "Implemented modern CSS Grid, Flexbox, and JavaScript for interactive features",
      techStack: ["HTML5", "CSS3", "JavaScript", "Responsive Design"],
      image: "üé¨", // Placeholder - replace with actual screenshot
      github: "#",
      demo: "#",
      color: "from-red-500 to-pink-600"
    },
    {
      title: "E-commerce Testing Suite",
      description: "Comprehensive testing framework for online shopping platform with automated test cases.",
      problem: "Manual testing was time-consuming and error-prone",
      solution: "Developed automated test suite covering user flows, API testing, and UI validation",
      techStack: ["Selenium", "Python", "Pytest", "API Testing"],
      image: "üõí",
      github: "#",
      demo: "#",
      color: "from-blue-500 to-purple-600"
    },
    {
      title: "Task Management App",
      description: "React-based productivity app with drag-and-drop functionality and real-time updates.",
      problem: "Need for intuitive task organization with team collaboration",
      solution: "Built responsive SPA with React, implementing drag-drop and real-time sync",
      techStack: ["React", "Node.js", "MongoDB", "Socket.io"],
      image: "üìã",
      github: "#",
      demo: "#",
      color: "from-green-500 to-teal-600"
    },
    {
      title: "API Testing Dashboard",
      description: "Automated API testing dashboard with real-time monitoring and detailed reporting.",
      problem: "Manual API testing across multiple environments was inefficient",
      solution: "Created dashboard for automated testing with visual reports and alerts",
      techStack: ["Postman", "Newman", "JavaScript", "Chart.js"],
      image: "üìä",
      github: "#",
      demo: "#",
      color: "from-yellow-500 to-orange-600"
    }
  ];

  return (
    <section className="py-20 px-4 bg-black">
      <div className="max-w-7xl mx-auto">
        <motion.h2 
          className="text-6xl md:text-8xl font-black text-center mb-20 ultra-sharp"
          initial={{ opacity: 0, y: 100, rotateX: -90 }}
          whileInView={{ opacity: 1, y: 0, rotateX: 0 }}
          transition={{ duration: 1.5, ease: "easeOut" }}
          viewport={{ once: true }}
        >
          <span className="holo-text neon-text float-3d animate-premium-glow">
            Featured Projects
          </span>
          {/* Enhanced glow layers */}
          <motion.div
            className="absolute inset-0 opacity-30 blur-lg"
            style={{
              background: 'linear-gradient(45deg, #c9b037, #e6e6fa, #b8860b, #daa520)',
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent'
            }}
            animate={{ opacity: [0.2, 0.5, 0.2] }}
            transition={{ duration: 3, repeat: Infinity }}
          >
            Featured Projects
          </motion.div>
        </motion.h2>

        <div className="grid lg:grid-cols-2 gap-8">
          {projects.map((project, index) => (
            <motion.div
              key={project.title}
              className="group relative electric-border holo-card rounded-3xl overflow-hidden backdrop-blur-xl transform-gpu animate-electric-pulse"
              initial={{ opacity: 0, y: 50, rotateX: -30 }}
              whileInView={{ opacity: 1, y: 0, rotateX: 0 }}
              transition={{ delay: index * 0.2, duration: 1 }}
              viewport={{ once: true }}
              whileHover={{ 
                scale: 1.05,
                rotateX: 8,
                rotateY: 8,
                boxShadow: '0 40px 100px rgba(255, 215, 0, 0.5), 0 0 120px rgba(0, 255, 255, 0.4)'
              }}
            >
              {/* Project Image/Preview */}
              <div className={`relative h-48 bg-gradient-to-br ${project.color} flex items-center justify-center overflow-hidden`}>
                <motion.div
                  className="text-6xl"
                  whileHover={{ scale: 1.2, rotate: 10 }}
                  transition={{ duration: 0.3 }}
                >
                  {project.image}
                </motion.div>

                {/* Overlay on hover */}
                <motion.div
                  className="absolute inset-0 bg-black/70 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300"
                  initial={{ opacity: 0 }}
                  whileHover={{ opacity: 1 }}
                >
                  <div className="flex space-x-4">
                    <motion.a
                      href={project.github}
                      className="px-4 py-2 bg-gray-800 text-white rounded-lg hover:bg-gray-700 transition-colors"
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      GitHub
                    </motion.a>
                    <motion.a
                      href={project.demo}
                      className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      Live Demo
                    </motion.a>
                  </div>
                </motion.div>
              </div>

              {/* Project Content */}
              <div className="p-6">
                <motion.h3 
                  className="text-3xl font-black holo-text animate-premium-glow mb-4 transition-colors"
                  whileHover={{ x: 8 }}
                >
                  {project.title}
                </motion.h3>

                <p className="text-gray-300 mb-4 leading-relaxed">
                  {project.description}
                </p>

                {/* Problem ‚Üí Solution */}
                <div className="space-y-3 mb-4">
                  <div className="flex items-start space-x-3">
                    <span className="text-red-400 font-semibold text-sm">Problem:</span>
                    <span className="text-gray-400 text-sm">{project.problem}</span>
                  </div>
                  <div className="flex items-start space-x-3">
                    <span className="text-green-400 font-semibold text-sm">Solution:</span>
                    <span className="text-gray-400 text-sm">{project.solution}</span>
                  </div>
                </div>

                {/* Tech Stack */}
                <div className="flex flex-wrap gap-2 mb-4">
                  {project.techStack.map((tech, techIndex) => (
                    <motion.span
                      key={tech}
                      className="px-3 py-1 bg-gray-800 text-blue-400 rounded-full text-sm border border-blue-400/30"
                      initial={{ opacity: 0, scale: 0.8 }}
                      whileInView={{ opacity: 1, scale: 1 }}
                      transition={{ delay: index * 0.1 + techIndex * 0.05, duration: 0.3 }}
                      viewport={{ once: true }}
                      whileHover={{ scale: 1.1, backgroundColor: "rgba(59, 130, 246, 0.2)" }}
                    >
                      {tech}
                    </motion.span>
                  ))}
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-4">
                  <motion.button
                    className="flex-1 py-2 bg-gradient-to-r from-blue-600 to-purple-600 text-white rounded-lg font-medium hover:from-blue-700 hover:to-purple-700 transition-all"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    View Details
                  </motion.button>
                  <motion.button
                    className="px-4 py-2 border border-gray-600 text-gray-300 rounded-lg hover:border-blue-400 hover:text-blue-400 transition-colors"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    ‚≠ê
                  </motion.button>
                </div>
              </div>

              {/* Glow effect */}
              <motion.div
                className={`absolute inset-0 bg-gradient-to-r ${project.color} opacity-0 group-hover:opacity-5 transition-opacity duration-500 pointer-events-none`}
              />
            </motion.div>
          ))}
        </div>

        {/* View More Projects Button */}
        <motion.div
          className="text-center mt-12"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5, duration: 0.6 }}
          viewport={{ once: true }}
        >
          <motion.button
            className="px-8 py-4 bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-lg font-semibold text-lg hover:from-purple-700 hover:to-pink-700 transition-all"
            whileHover={{ 
              scale: 1.05,
              boxShadow: "0 10px 30px rgba(147, 51, 234, 0.3)"
            }}
            whileTap={{ scale: 0.95 }}
          >
            View All Projects ‚Üí
          </motion.button>
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
