import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  return (
    <section className="min-h-screen flex items-center justify-center px-4 relative overflow-hidden">
      {/* Advanced 3D Floating Orbs */}
      <div className="absolute inset-0">
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute rounded-full"
            style={{
              width: `${Math.random() * 100 + 50}px`,
              height: `${Math.random() * 100 + 50}px`,
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              background: `radial-gradient(circle, ${[
                'rgba(0, 243, 255, 0.3)',
                'rgba(191, 0, 255, 0.3)',
                'rgba(255, 0, 128, 0.3)'
              ][Math.floor(Math.random() * 3)]} 0%, transparent 70%)`,
              filter: 'blur(2px)'
            }}
            animate={{
              y: [-30, 30, -30],
              x: [-20, 20, -20],
              scale: [1, 1.2, 1],
              rotate: [0, 180, 360],
            }}
            transition={{
              duration: 8 + Math.random() * 4,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          />
        ))}
      </div>

      {/* Quantum Grid Background */}
      <div className="absolute inset-0 opacity-20">
        <div className="w-full h-full" style={{
          backgroundImage: `
            linear-gradient(rgba(0, 243, 255, 0.1) 1px, transparent 1px),
            linear-gradient(90deg, rgba(0, 243, 255, 0.1) 1px, transparent 1px)
          `,
          backgroundSize: '50px 50px',
          animation: 'grid-pulse 4s ease-in-out infinite'
        }} />
      </div>

      <div className="text-center z-10 ultra-sharp">
        {/* 3D Holographic Name */}
        <motion.div
          className="relative mb-8"
          initial={{ scale: 0.5, opacity: 0, rotateY: -180 }}
          animate={{ scale: 1, opacity: 1, rotateY: 0 }}
          transition={{ duration: 1.5, ease: "easeOut" }}
        >
          <motion.h1 
            className="text-8xl md:text-9xl font-black mb-6 relative float-3d"
            whileHover={{ scale: 1.05, rotateY: 10, rotateX: 5 }}
            transition={{ duration: 0.3 }}
          >
            <span className="holo-text neon-text animate-premium-glow">
              YASH KUMAR OM
            </span>

            {/* Enhanced 3D Depth Layers */}
            <motion.div
              className="absolute inset-0 opacity-40 blur-sm"
              style={{ 
                transform: 'translateZ(-10px) translateX(3px) translateY(3px)',
                background: 'linear-gradient(45deg, #ffd700, #00ffff, #ff1493)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent'
              }}
              animate={{ opacity: [0.2, 0.6, 0.2] }}
              transition={{ duration: 4, repeat: Infinity }}
            >
              YASH KUMAR OM
            </motion.div>

            <motion.div
              className="absolute inset-0 opacity-20 blur-md"
              style={{ 
                transform: 'translateZ(-20px) translateX(6px) translateY(6px)',
                background: 'linear-gradient(45deg, #39ff14, #ff6600, #bf00ff)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent'
              }}
              animate={{ opacity: [0.1, 0.4, 0.1] }}
              transition={{ duration: 5, repeat: Infinity }}
            >
              YASH KUMAR OM
            </motion.div>
          </motion.h1>
        </motion.div>

        {/* Holographic Tagline */}
        <motion.div
          className="mb-12"
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 0.8, duration: 1 }}
        >
          <div className="holo-card p-6 rounded-2xl backdrop-blur-xl">
            <p className="text-2xl md:text-3xl text-white leading-relaxed font-light">
              <motion.span 
                className="text-premium-gold font-bold animate-premium-pulse"
                animate={{ 
                  textShadow: [
                    '0 0 20px #c9b037, 0 0 40px #c9b037, 0 0 60px #c9b037',
                    '0 0 30px #e6e6fa, 0 0 60px #e6e6fa, 0 0 90px #e6e6fa',
                    '0 0 20px #c9b037, 0 0 40px #c9b037, 0 0 60px #c9b037'
                  ]
                }}
                transition={{ duration: 3, repeat: Infinity }}
              >
                QA Engineer
              </motion.span>
              <span className="mx-4 text-champagne animate-pulse">|</span>
              <motion.span 
                className="text-lavender-mist font-bold animate-premium-pulse"
                animate={{ 
                  textShadow: [
                    '0 0 20px #e6e6fa, 0 0 40px #e6e6fa, 0 0 60px #e6e6fa',
                    '0 0 30px #b8860b, 0 0 60px #b8860b, 0 0 90px #b8860b',
                    '0 0 20px #e6e6fa, 0 0 40px #e6e6fa, 0 0 60px #e6e6fa'
                  ]
                }}
                transition={{ duration: 3, repeat: Infinity, delay: 1 }}
              >
                Frontend Developer
              </motion.span>
              <span className="mx-4 text-antique-gold animate-pulse">|</span>
              <motion.span 
                className="text-golden-rod font-bold animate-premium-pulse"
                animate={{ 
                  textShadow: [
                    '0 0 20px #daa520, 0 0 40px #daa520, 0 0 60px #daa520',
                    '0 0 30px #f7e7ce, 0 0 60px #f7e7ce, 0 0 90px #f7e7ce',
                    '0 0 20px #daa520, 0 0 40px #daa520, 0 0 60px #daa520'
                  ]
                }}
                transition={{ duration: 3, repeat: Infinity, delay: 2 }}
              >
                Building Reliable Digital Products
              </motion.span>
            </p>
          </div>
        </motion.div>

        {/* Quantum CTA Buttons */}
        <motion.div
          className="flex flex-col sm:flex-row gap-8 justify-center items-center"
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 1.2, duration: 1 }}
        >
          <motion.button 
            className="group relative px-10 py-5 electric-border holo-card rounded-2xl font-bold text-xl overflow-hidden transform-gpu"
            whileHover={{ 
              scale: 1.15, 
              rotateY: 15,
              rotateX: 10,
              boxShadow: '0 30px 80px rgba(255, 215, 0, 0.6), 0 0 100px rgba(0, 255, 255, 0.4)'
            }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            <span className="relative z-10 holo-text animate-rainbow-glow">Download Resume</span>
            <motion.div
              className="absolute inset-0 bg-gradient-to-r from-neon-gold via-cosmic-pink to-cosmic-cyan opacity-0 group-hover:opacity-30"
              whileHover={{ opacity: 0.4 }}
              transition={{ duration: 0.3 }}
            />
          </motion.button>

          <motion.button 
            className="group relative px-10 py-5 electric-border holo-card rounded-2xl font-bold text-xl transform-gpu"
            whileHover={{ 
              scale: 1.15,
              rotateY: -15,
              rotateX: 10,
              boxShadow: '0 30px 80px rgba(255, 20, 147, 0.6), 0 0 100px rgba(57, 255, 20, 0.4)'
            }}
            whileTap={{ scale: 0.95 }}
            transition={{ type: "spring", stiffness: 300 }}
          >
            <span className="holo-text animate-rainbow-glow">Hire Me</span>
            <motion.div
              className="absolute inset-0 bg-gradient-to-r from-cosmic-pink via-neon-lime to-electric-purple opacity-0 group-hover:opacity-30"
              whileHover={{ opacity: 0.4 }}
              transition={{ duration: 0.3 }}
            />
          </motion.button>
        </motion.div>

        {/* 3D Scroll Indicator */}
        <motion.div
          className="absolute bottom-10 left-1/2 transform -translate-x-1/2"
          animate={{ 
            y: [0, 15, 0],
            rotateX: [0, 10, 0]
          }}
          transition={{ duration: 3, repeat: Infinity }}
        >
          <div className="holo-card w-8 h-16 rounded-full flex justify-center p-2">
            <motion.div
              className="w-2 h-6 bg-gradient-to-b from-cyan-400 to-purple-400 rounded-full"
              animate={{ 
                opacity: [1, 0.3, 1],
                scaleY: [1, 0.5, 1]
              }}
              transition={{ duration: 2, repeat: Infinity }}
            />
          </div>
        </motion.div>
      </div>

      <style jsx>{`
        @keyframes grid-pulse {
          0%, 100% { opacity: 0.2; }
          50% { opacity: 0.4; }
        }
      `}</style>
    </section>
  );
};

export default Hero;
