import React from 'react';
import { motion } from 'framer-motion';

const Achievements = () => {
  const achievements = [
    {
      category: "Internships",
      icon: "üíº",
      color: "from-blue-500 to-cyan-500",
      items: [
        {
          title: "QA Engineer Intern",
          organization: "Tech Solutions Inc.",
          duration: "Jun 2023 - Aug 2023",
          description: "Developed automated test scripts and improved testing efficiency by 40%",
          skills: ["Selenium", "Python", "API Testing"]
        },
        {
          title: "Frontend Developer Intern",
          organization: "Digital Agency Pro",
          duration: "Jan 2023 - Mar 2023",
          description: "Built responsive web components and collaborated on 3 client projects",
          skills: ["React", "JavaScript", "CSS"]
        }
      ]
    },
    {
      category: "Certifications",
      icon: "üèÜ",
      color: "from-purple-500 to-pink-500",
      items: [
        {
          title: "ISTQB Foundation Level",
          organization: "International Software Testing Board",
          duration: "2023",
          description: "Certified in software testing fundamentals and best practices",
          skills: ["Testing Theory", "Test Design", "Test Management"]
        },
        {
          title: "React Developer Certification",
          organization: "Meta (Facebook)",
          duration: "2023",
          description: "Advanced React concepts, hooks, and modern development practices",
          skills: ["React", "Hooks", "State Management"]
        },
        {
          title: "Selenium WebDriver",
          organization: "Udemy",
          duration: "2022",
          description: "Comprehensive automation testing with Selenium and Java",
          skills: ["Selenium", "Java", "TestNG"]
        }
      ]
    },
    {
      category: "Hackathons",
      icon: "üöÄ",
      color: "from-green-500 to-teal-500",
      items: [
        {
          title: "Smart City Solutions",
          organization: "TechFest 2023",
          duration: "48 hours",
          description: "Built IoT-based traffic management system - 2nd Place Winner",
          skills: ["IoT", "React", "Node.js"]
        },
        {
          title: "FinTech Innovation Challenge",
          organization: "CodeSprint 2023",
          duration: "36 hours",
          description: "Developed secure payment gateway with fraud detection",
          skills: ["Security", "APIs", "Machine Learning"]
        }
      ]
    }
  ];

  const stats = [
    { label: "Projects Completed", value: "15+", icon: "üìÅ" },
    { label: "Bugs Found & Fixed", value: "500+", icon: "üêõ" },
    { label: "Test Cases Written", value: "1000+", icon: "‚úÖ" },
    { label: "Hours of Testing", value: "2000+", icon: "‚è±Ô∏è" }
  ];

  return (
    <section className="py-20 px-4 bg-black">
      <div className="max-w-7xl mx-auto">
        <motion.h2 
          className="text-6xl md:text-8xl font-black text-center mb-20 ultra-sharp"
          initial={{ opacity: 0, y: 100, rotateX: -90 }}
          whileInView={{ opacity: 1, y: 0, rotateX: 0 }}
          transition={{ duration: 1.5, ease: "easeOut" }}
          viewport={{ once: true }}
        >
          <span className="text-white" style={{
            textShadow: '0 0 4px rgba(0, 255, 255, 0.2), 0 0 8px rgba(0, 255, 255, 0.1)'
          }}>
            Achievements & Recognition
          </span>
        </motion.h2>

        {/* Stats Section */}
        <motion.div
          className="grid grid-cols-2 md:grid-cols-4 gap-6 mb-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.2, duration: 0.8 }}
          viewport={{ once: true }}
        >
          {stats.map((stat, index) => (
            <motion.div
              key={stat.label}
              className="text-center electric-border holo-card p-8 rounded-2xl backdrop-blur-xl transform-gpu animate-electric-pulse"
              initial={{ opacity: 0, scale: 0.8, rotateY: -30 }}
              whileInView={{ opacity: 1, scale: 1, rotateY: 0 }}
              transition={{ delay: index * 0.1, duration: 0.8 }}
              viewport={{ once: true }}
              whileHover={{ 
                scale: 1.1,
                rotateY: 10,
                boxShadow: "0 30px 80px rgba(255, 215, 0, 0.4), 0 0 100px rgba(0, 255, 255, 0.3)"
              }}
            >
              <motion.div
                className="text-6xl mb-4"
                whileHover={{ scale: 1.4, rotate: 15 }}
                transition={{ duration: 0.3 }}
              >
                {stat.icon}
              </motion.div>
              <motion.div
                className="text-4xl font-black holo-text animate-premium-glow mb-3"
                initial={{ opacity: 0 }}
                whileInView={{ opacity: 1 }}
                transition={{ delay: index * 0.1 + 0.3, duration: 0.5 }}
                viewport={{ once: true }}
              >
                {stat.value}
              </motion.div>
              <div className="text-gray-300 text-lg font-medium">{stat.label}</div>
            </motion.div>
          ))}
        </motion.div>

        {/* Achievements Categories */}
        <div className="space-y-12">
          {achievements.map((category, categoryIndex) => (
            <motion.div
              key={category.category}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ delay: categoryIndex * 0.2, duration: 0.8 }}
              viewport={{ once: true }}
            >
              {/* Category Header */}
              <motion.div
                className="flex items-center justify-center mb-8"
                whileHover={{ scale: 1.05 }}
                transition={{ duration: 0.3 }}
              >
                <div className={`flex items-center space-x-4 px-8 py-4 bg-gradient-to-r ${category.color} rounded-full`}>
                  <span className="text-3xl">{category.icon}</span>
                  <h3 className="text-2xl font-bold text-white">{category.category}</h3>
                </div>
              </motion.div>

              {/* Achievement Cards */}
              <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                {category.items.map((item, itemIndex) => (
                  <motion.div
                    key={item.title}
                    className="group relative bg-gray-900/50 rounded-xl p-6 backdrop-blur-sm border border-gray-800 hover:border-blue-400/50 transition-all duration-500 overflow-hidden"
                    initial={{ opacity: 0, y: 30 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ delay: itemIndex * 0.1, duration: 0.6 }}
                    viewport={{ once: true }}
                    whileHover={{ 
                      scale: 1.02,
                      rotateY: 5,
                      rotateX: 5,
                    }}
                  >
                    {/* Background Glow */}
                    <motion.div
                      className={`absolute inset-0 bg-gradient-to-br ${category.color} opacity-0 group-hover:opacity-10 transition-opacity duration-500`}
                      whileHover={{ scale: 1.1 }}
                    />

                    {/* Content */}
                    <div className="relative z-10">
                      <motion.h4 
                        className="text-xl font-bold text-white mb-2 group-hover:text-blue-400 transition-colors"
                        whileHover={{ x: 5 }}
                      >
                        {item.title}
                      </motion.h4>

                      <div className="text-gray-400 mb-2 font-medium">
                        {item.organization}
                      </div>

                      <div className="text-sm text-blue-400 mb-4">
                        {item.duration}
                      </div>

                      <p className="text-gray-300 mb-4 leading-relaxed">
                        {item.description}
                      </p>

                      {/* Skills Tags */}
                      <div className="flex flex-wrap gap-2">
                        {item.skills.map((skill, skillIndex) => (
                          <motion.span
                            key={skill}
                            className="px-3 py-1 bg-gray-800 text-gray-300 rounded-full text-xs border border-gray-700 hover:border-blue-400 hover:text-blue-400 transition-colors"
                            initial={{ opacity: 0, scale: 0.8 }}
                            whileInView={{ opacity: 1, scale: 1 }}
                            transition={{ delay: itemIndex * 0.1 + skillIndex * 0.05, duration: 0.3 }}
                            viewport={{ once: true }}
                            whileHover={{ scale: 1.1 }}
                          >
                            {skill}
                          </motion.span>
                        ))}
                      </div>
                    </div>

                    {/* Hover Effect Lines */}
                    <motion.div
                      className="absolute top-0 left-0 w-full h-0.5 bg-gradient-to-r from-transparent via-blue-400 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"
                    />
                    <motion.div
                      className="absolute bottom-0 left-0 w-full h-0.5 bg-gradient-to-r from-transparent via-purple-400 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500"
                    />
                  </motion.div>
                ))}
              </div>
            </motion.div>
          ))}
        </div>

        {/* Call to Action */}
        <motion.div
          className="text-center mt-16"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5, duration: 0.6 }}
          viewport={{ once: true }}
        >
          <motion.button
            className="px-8 py-4 bg-gradient-to-r from-yellow-500 to-orange-600 text-black font-bold rounded-lg text-lg hover:from-yellow-600 hover:to-orange-700 transition-all"
            whileHover={{ 
              scale: 1.05,
              boxShadow: "0 10px 30px rgba(251, 191, 36, 0.3)"
            }}
            whileTap={{ scale: 0.95 }}
          >
            View All Certificates ‚Üí
          </motion.button>
        </motion.div>
      </div>
    </section>
  );
};

export default Achievements;
