import React from 'react';
import { motion } from 'framer-motion';

const Features = () => {
  const features = [
    {
      icon: 'ðŸŽ®',
      title: 'Gaming UI/UX',
      description: 'Interactive gaming interfaces with smooth animations and responsive controls',
      tech: ['React', 'Framer Motion', 'CSS3'],
      color: '#00D4FF'
    },
    {
      icon: 'âš¡',
      title: 'Performance Optimization',
      description: 'High-performance applications with 60fps animations and optimized rendering',
      tech: ['WebGL', 'Canvas API', 'React Fiber'],
      color: '#39FF14'
    },
    {
      icon: 'ðŸ”®',
      title: 'AR/VR Integration',
      description: 'Immersive experiences using WebXR and Three.js for next-gen applications',
      tech: ['Three.js', 'WebXR', 'A-Frame'],
      color: '#FF1493'
    },
    {
      icon: 'ðŸš€',
      title: 'Real-time Features',
      description: 'Live chat, multiplayer gaming, and real-time data synchronization',
      tech: ['Socket.io', 'WebRTC', 'Firebase'],
      color: '#FF6B35'
    }
  ];

  return (
    <section className="py-32 px-4 relative overflow-hidden">
      <div className="max-w-7xl mx-auto relative z-10">
        <motion.div
          className="text-center mb-20"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
          viewport={{ once: true }}
        >
          <h2 className="text-6xl md:text-8xl font-black mb-6">
            <span style={{
              background: 'linear-gradient(45deg, #00D4FF, #39FF14, #FF1493, #FF6B35)',
              backgroundClip: 'text',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              textShadow: '0 0 30px rgba(0, 212, 255, 0.5)'
            }}>
              Gaming Features
            </span>
          </h2>
          <p className="text-xl max-w-3xl mx-auto" style={{ color: '#E0E6ED' }}>
            Next-generation portfolio features designed for modern gaming and interactive experiences
          </p>
        </motion.div>

        <div className="grid md:grid-cols-2 lg:grid-cols-2 gap-8">
          {features.map((feature, index) => (
            <motion.div
              key={index}
              className="gaming-card p-8 rounded-2xl backdrop-blur-xl transform-gpu"
              initial={{ opacity: 0, y: 100, rotateX: -30 }}
              whileInView={{ opacity: 1, y: 0, rotateX: 0 }}
              transition={{ 
                delay: index * 0.1, 
                duration: 0.8,
                type: "spring",
                stiffness: 100
              }}
              viewport={{ once: true }}
              whileHover={{
                scale: 1.05,
                rotateY: 10
              }}
            >
              <motion.div
                className="text-6xl mb-6"
                whileHover={{ scale: 1.2, rotate: 15 }}
                transition={{ duration: 0.3 }}
              >
                {feature.icon}
              </motion.div>

              <h3 
                className="text-2xl font-bold mb-4"
                style={{ color: feature.color }}
              >
                {feature.title}
              </h3>

              <p className="mb-6 leading-relaxed" style={{ color: '#E0E6ED' }}>
                {feature.description}
              </p>

              <div className="flex flex-wrap gap-2">
                {feature.tech.map((tech, techIndex) => (
                  <motion.span
                    key={techIndex}
                    className="px-3 py-1 text-sm rounded-full"
                    style={{
                      background: `${feature.color}20`,
                      border: `1px solid ${feature.color}60`,
                      color: feature.color
                    }}
                    whileHover={{ scale: 1.1 }}
                    transition={{ duration: 0.2 }}
                  >
                    {tech}
                  </motion.span>
                ))}
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Features;
