import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  return (
    <section className="min-h-screen flex items-center justify-center px-4 relative overflow-hidden">
      {/* Premium Floating Orbs */}
      <div className="absolute inset-0">
        {[...Array(20)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute rounded-full"
            style={{
              width: `${Math.random() * 100 + 50}px`,
              height: `${Math.random() * 100 + 50}px`,
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              background: `radial-gradient(circle, ${[
                'rgba(201, 176, 55, 0.3)',
                'rgba(230, 230, 250, 0.3)',
                'rgba(184, 134, 11, 0.3)'
              ][Math.floor(Math.random() * 3)]} 0%, transparent 70%)`,
              filter: 'blur(2px)'
            }}
            animate={{
              y: [-30, 30, -30],
              x: [-20, 20, -20],
              scale: [1, 1.2, 1],
              rotate: [0, 180, 360],
            }}
            transition={{
              duration: 8 + Math.random() * 4,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          />
        ))}
      </div>

      {/* Main Content Grid */}
      <div className="max-w-7xl mx-auto w-full z-10">
        <div className="grid lg:grid-cols-2 gap-16 items-center">
          {/* Left Side - Photo */}
          <motion.div
            className="relative order-2 lg:order-1"
            initial={{ opacity: 0, x: -100, rotateY: -45 }}
            animate={{ opacity: 1, x: 0, rotateY: 0 }}
            transition={{ duration: 1.5, ease: "easeOut" }}
          >
            <div className="relative w-96 h-96 mx-auto transform-gpu">
              {/* Enhanced Photo Frame */}
              <motion.div
                className="absolute inset-0 electric-border rounded-full"
                animate={{
                  rotate: [0, 360],
                  scale: [1, 1.08, 1]
                }}
                transition={{
                  rotate: { duration: 15, repeat: Infinity, ease: "linear" },
                  scale: { duration: 5, repeat: Infinity, ease: "easeInOut" }
                }}
              />

              {/* Multiple rotating rings */}
              <motion.div
                className="absolute inset-0 border-4 border-premium-gold rounded-full opacity-80"
                style={{ transform: 'scale(1.1)' }}
                animate={{ rotate: [360, 0] }}
                transition={{ duration: 20, repeat: Infinity, ease: "linear" }}
              />

              <motion.div
                className="absolute inset-0 border-2 border-champagne rounded-full opacity-60"
                style={{ transform: 'scale(1.2)' }}
                animate={{ rotate: [0, 360] }}
                transition={{ duration: 25, repeat: Infinity, ease: "linear" }}
              />

              {/* Photo Container */}
              <motion.div
                className="relative w-full h-full holo-card rounded-full overflow-hidden transform-gpu"
                whileHover={{ 
                  scale: 1.1,
                  rotateY: 15,
                  rotateX: 10,
                  boxShadow: '0 30px 80px rgba(201, 176, 55, 0.6)'
                }}
                transition={{ duration: 0.5, ease: "easeOut" }}
              >
                <img 
                  src="/yash-photo.jpg" 
                  alt="Yash Kumar Om"
                  className="w-full h-full object-cover transform-gpu"
                  style={{
                    filter: 'contrast(1.2) saturate(1.3) brightness(1.1)',
                    opacity: 1,
                    visibility: 'visible'
                  }}
                  onError={(e) => {
                    e.target.style.display = 'none';
                    e.target.nextSibling.style.display = 'flex';
                  }}
                />
                {/* Fallback gradient */}
                <div className="w-full h-full bg-gradient-to-br from-premium-gold via-champagne to-lavender-mist flex items-center justify-center text-6xl font-bold holo-text" style={{display: 'none'}}>
                  YKO
                </div>

                {/* Enhanced Holographic Overlay */}
                <motion.div
                  className="absolute inset-0 bg-gradient-to-br from-premium-gold/20 via-transparent to-champagne/20 opacity-0 hover:opacity-100 transition-opacity duration-500"
                />
              </motion.div>

              {/* Floating Rings */}
              {[...Array(5)].map((_, i) => (
                <motion.div
                  key={i}
                  className={`absolute inset-0 border rounded-full ${
                    i === 0 ? 'border-premium-gold' : 
                    i === 1 ? 'border-champagne' : 
                    i === 2 ? 'border-lavender-mist' : 
                    i === 3 ? 'border-antique-gold' : 'border-golden-rod'
                  }`}
                  style={{
                    transform: `scale(${1 + i * 0.1})`,
                    opacity: 0.8 - i * 0.1
                  }}
                  animate={{
                    rotate: [0, 360],
                    opacity: [0.3, 0.9, 0.3]
                  }}
                  transition={{
                    rotate: { duration: 10 + i * 2, repeat: Infinity, ease: "linear" },
                    opacity: { duration: 3 + i, repeat: Infinity, ease: "easeInOut" }
                  }}
                />
              ))}
            </div>
          </motion.div>

          {/* Right Side - Name and Details */}
          <motion.div
            className="text-left order-1 lg:order-2"
            initial={{ opacity: 0, x: 100 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 1.2, ease: "easeOut" }}
          >
            {/* 3D Holographic Name */}
            <motion.h1 
              className="text-6xl md:text-7xl font-black mb-6 relative float-3d ultra-sharp"
              whileHover={{ scale: 1.05, rotateY: 10, rotateX: 5 }}
              transition={{ duration: 0.3 }}
              style={{ opacity: 1, visibility: 'visible' }}
            >
              <span style={{
                background: 'linear-gradient(135deg, #FFD700, #FF6347, #FF1493, #00CED1)',
                backgroundClip: 'text',
                WebkitBackgroundClip: 'text',
                WebkitTextFillColor: 'transparent',
                fontWeight: '900',
                textShadow: '0 0 40px rgba(255, 215, 0, 0.8)',
                opacity: 1,
                visibility: 'visible'
              }}>
                YASH KUMAR OM
              </span>

              {/* Enhanced 3D Depth Layers */}
              <motion.div
                className="absolute inset-0 opacity-50 blur-sm"
                style={{ 
                  transform: 'translateZ(-10px) translateX(4px) translateY(4px)',
                  background: 'linear-gradient(45deg, #c9b037, #e6e6fa, #b8860b)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  opacity: 1,
                  visibility: 'visible'
                }}
                animate={{ opacity: [0.3, 0.7, 0.3] }}
                transition={{ duration: 4, repeat: Infinity }}
              >
                YASH KUMAR OM
              </motion.div>

              <motion.div
                className="absolute inset-0 opacity-30 blur-md"
                style={{ 
                  transform: 'translateZ(-20px) translateX(8px) translateY(8px)',
                  background: 'linear-gradient(45deg, #daa520, #f7e7ce, #b8860b)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                  opacity: 1,
                  visibility: 'visible'
                }}
                animate={{ opacity: [0.2, 0.5, 0.2] }}
                transition={{ duration: 5, repeat: Infinity }}
              >
                YASH KUMAR OM
              </motion.div>
            </motion.h1>

            {/* Professional Tagline */}
            <motion.div
              className="mb-8"
              initial={{ y: 50, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.8, duration: 1 }}
            >
              <div className="holo-card p-6 rounded-2xl backdrop-blur-xl">
                <p className="text-lg md:text-xl text-white leading-relaxed font-light">
                  <motion.span 
                    style={{
                      color: '#FFD700',
                      fontWeight: '700',
                      textShadow: '0 0 20px rgba(255, 215, 0, 0.6)',
                      opacity: 1,
                      visibility: 'visible'
                    }}
                  >
                    Software Quality Assurance Engineer
