import React from 'react';
import { motion } from 'framer-motion';

const Features = () => {
  const services = [
    {
      icon: "ðŸ’»",
      title: "Web Development",
      description: "Creating responsive and modern web applications using latest technologies like React, Node.js, and modern frameworks.",
      color: "from-cyan-400 to-blue-500"
    },
    {
      icon: "ðŸŽ¨",
      title: "Graphic Design",
      description: "Designing beautiful and functional user interfaces with focus on user experience and modern design principles.",
      color: "from-blue-400 to-purple-500"
    },
    {
      icon: "ðŸ“±",
      title: "Digital Marketing",
      description: "Building engaging content strategies and growing online presence through YouTube, social media, and digital platforms.",
      color: "from-purple-400 to-pink-500"
    }
  ];

  return (
    <section id="services" className="py-20 px-6 relative overflow-hidden">
      {/* Background Elements */}
      <div className="absolute inset-0">
        <div className="absolute top-20 right-20 w-96 h-96 bg-cyan-500/5 rounded-full blur-3xl"></div>
        <div className="absolute bottom-20 left-20 w-80 h-80 bg-blue-500/5 rounded-full blur-3xl"></div>
      </div>

      <div className="max-w-7xl mx-auto relative z-10">
        {/* Section Header */}
        <motion.div
          className="text-center mb-16"
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
          viewport={{ once: true }}
        >
          <motion.h2 
            className="text-4xl md:text-5xl font-bold text-white mb-4"
            initial={{ opacity: 0, scale: 0.8 }}
            whileInView={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, ease: "easeOut" }}
            viewport={{ once: true }}
          >
            <motion.span
              className="inline-block"
              initial={{ y: 20, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.2, duration: 0.6 }}
              viewport={{ once: true }}
            >
              My
            </motion.span>
            {" "}
            <motion.span 
              className="text-cyan-400 inline-block"
              initial={{ y: 20, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.4, duration: 0.6 }}
              viewport={{ once: true }}
              whileHover={{ 
                scale: 1.05,
                textShadow: "0 0 20px rgba(0, 255, 255, 0.6)"
              }}
            >
              Services
            </motion.span>
          </motion.h2>
          <motion.p 
            className="text-gray-300 text-lg max-w-2xl mx-auto"
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.6, duration: 0.6 }}
            viewport={{ once: true }}
          >
            Comprehensive digital solutions tailored to meet your business needs and drive growth
          </motion.p>
        </motion.div>

        {/* Services Grid */}
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <motion.div
              key={service.title}
              className="gaming-card p-8 text-center group cursor-pointer relative overflow-hidden"
              initial={{ opacity: 0, y: 60, rotateX: -20, scale: 0.9 }}
              whileInView={{ opacity: 1, y: 0, rotateX: 0, scale: 1 }}
              transition={{ delay: index * 0.2, duration: 1, ease: "easeOut" }}
              viewport={{ once: true }}
              whileHover={{ 
                scale: 1.08,
                y: -15,
                rotateY: 10,
                rotateX: 5,
                transition: { duration: 0.4, ease: "easeOut" }
              }}
            >
              {/* Animated background layers */}
              <motion.div
                className={`absolute inset-0 bg-gradient-to-br ${service.color} opacity-0 group-hover:opacity-15 transition-opacity duration-700 rounded-2xl`}
                whileHover={{ scale: 1.1, rotate: 2 }}
              />

              {/* Rotating border glow */}
              <motion.div
                className="absolute inset-0 rounded-2xl opacity-0 group-hover:opacity-100 transition-opacity duration-500"
                style={{
                  background: `conic-gradient(from 0deg, transparent, rgba(0, 255, 255, 0.3), transparent, rgba(0, 255, 255, 0.3), transparent)`,
                  padding: '2px'
                }}
                animate={{
                  rotate: [0, 360]
                }}
                transition={{
                  duration: 4,
                  repeat: Infinity,
                  ease: "linear"
                }}
              />

              {/* Inner content container */}
              <div className="relative z-10">
                {/* Icon with multiple effects */}
                <motion.div
                  className="text-6xl mb-6 relative inline-block"
                  whileHover={{ 
                    scale: 1.4, 
                    rotate: [0, -10, 10, 0],
                    filter: "drop-shadow(0 0 30px rgba(0, 255, 255, 0.8))"
                  }}
                  transition={{ duration: 0.6, ease: "easeOut" }}
                >
                  <motion.span
                    animate={{
                      textShadow: [
                        "0 0 20px rgba(0, 255, 255, 0.5)",
                        "0 0 40px rgba(0, 255, 255, 0.8)",
                        "0 0 20px rgba(0, 255, 255, 0.5)"
                      ]
                    }}
                    transition={{
                      duration: 2,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  >
                    {service.icon}
                  </motion.span>

                  {/* Icon particles */}
                  <motion.div
                    className="absolute -top-2 -right-2 w-3 h-3 bg-cyan-400 rounded-full opacity-0 group-hover:opacity-100"
                    animate={{
                      y: [-5, 5, -5],
                      x: [2, -2, 2]
                    }}
                    transition={{
                      duration: 2,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  />
                  <motion.div
                    className="absolute -bottom-1 -left-1 w-2 h-2 bg-blue-400 rounded-full opacity-0 group-hover:opacity-100"
                    animate={{
                      y: [3, -3, 3],
                      x: [-1, 1, -1]
                    }}
                    transition={{
                      duration: 1.5,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  />
                </motion.div>

                {/* Title with typewriter effect on hover */}
                <motion.h3 
                  className="text-2xl font-bold mb-4 relative overflow-hidden"
                  style={{ color: 'white' }}
                  whileHover={{ 
                    y: -3,
                    textShadow: "0 0 20px rgba(0, 255, 255, 0.6)"
                  }}
                >
                  <motion.span
                    className="group-hover:text-transparent group-hover:bg-clip-text group-hover:bg-gradient-to-r group-hover:from-cyan-400 group-hover:via-blue-500 group-hover:to-purple-500 transition-all duration-500"
                  >
                    {service.title}
                  </motion.span>

                  {/* Animated underline */}
                  <motion.div
                    className="absolute bottom-0 left-0 h-0.5 bg-gradient-to-r from-cyan-400 to-blue-500 opacity-0 group-hover:opacity-100"
                    initial={{ width: 0 }}
                    whileHover={{ width: '100%' }}
                    transition={{ duration: 0.6, ease: "easeOut" }}
                  />
                </motion.h3>

                {/* Description with reveal animation */}
                <motion.p 
                  className="text-gray-300 leading-relaxed mb-6 group-hover:text-gray-200 transition-colors duration-300"
                  whileHover={{ scale: 1.02 }}
                >
                  {service.description}
