import React from 'react';
import { motion } from 'framer-motion';

const Projects = () => {
  const projects = [
    {
      title: "Gaming YouTube Channel",
      description: "Content creation channel with 10K+ subscribers featuring gaming tutorials, reviews, and live streams.",
      problem: "Need to build engaging gaming community and consistent content",
      solution: "Created diverse content strategy with tutorials, gameplay, and community interaction",
      techStack: ["Video Editing", "OBS Studio", "Photoshop", "Social Media"],
      image: "üéÆ",
      github: "#",
      demo: "#",
      color: "from-red-500 to-pink-600"
    },
    {
      title: "Mobile App Portfolio",
      description: "Cross-platform mobile applications built with React Native and Flutter for various clients.",
      problem: "Clients needed modern, responsive mobile solutions",
      solution: "Developed scalable mobile apps with clean UI and smooth performance",
      techStack: ["React Native", "Flutter", "Firebase", "API Integration"],
      image: "üì±",
      github: "#",
      demo: "#",
      color: "from-blue-500 to-purple-600"
    },
    {
      title: "Web Development Projects",
      description: "Full-stack web applications with modern frameworks and responsive design principles.",
      problem: "Businesses needed modern web presence with great UX",
      solution: "Built responsive websites with React, Node.js and modern design patterns",
      techStack: ["React", "Node.js", "MongoDB", "Tailwind CSS"],
      image: "üíª",
      github: "#",
      demo: "#",
      color: "from-green-500 to-teal-600"
    },
    {
      title: "UI/UX Design Portfolio",
      description: "Creative design solutions for brands, apps, and digital experiences with modern aesthetics.",
      problem: "Clients needed visually appealing and user-friendly designs",
      solution: "Created comprehensive design systems with focus on user experience",
      techStack: ["Figma", "Adobe Creative Suite", "Sketch", "Prototyping"],
      image: "üé®",
      github: "#",
      demo: "#",
      color: "from-purple-500 to-indigo-600"
    }
  ];

  return (
    <section className="py-20 px-4 relative overflow-hidden">
      {/* Gaming Background */}
      <div className="absolute inset-0">
        {[...Array(12)].map((_, i) => (
          <motion.div
            key={i}
            className="absolute rounded-full"
            style={{
              width: `${Math.random() * 120 + 40}px`,
              height: `${Math.random() * 120 + 40}px`,
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              background: `radial-gradient(circle, ${[
                'rgba(0, 255, 255, 0.08)',
                'rgba(128, 0, 255, 0.08)',
                'rgba(0, 128, 255, 0.08)'
              ][Math.floor(Math.random() * 3)]} 0%, transparent 70%)`,
            }}
            animate={{
              y: [-25, 25, -25],
              x: [-20, 20, -20],
              scale: [1, 1.15, 1],
            }}
            transition={{
              duration: 12 + Math.random() * 6,
              repeat: Infinity,
              ease: "easeInOut",
            }}
          />
        ))}
      </div>

      <div className="max-w-7xl mx-auto relative z-10">
        <motion.h2 
          className="text-6xl md:text-8xl font-black text-center mb-20"
          initial={{ opacity: 0, y: 100 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 1.5 }}
          viewport={{ once: true }}
        >
          <span className="text-gaming-gradient hover-glow">
            Featured Projects
          </span>
        </motion.h2>

        <div className="grid lg:grid-cols-2 gap-8">
          {projects.map((project, index) => (
            <motion.div
              key={project.title}
              className="gaming-card hover-gaming rounded-3xl overflow-hidden backdrop-blur-xl transform-gpu"
              initial={{ opacity: 0, y: 50, rotateX: -30 }}
              whileInView={{ opacity: 1, y: 0, rotateX: 0 }}
              transition={{ delay: index * 0.2, duration: 1 }}
              viewport={{ once: true }}
              whileHover={{ 
                scale: 1.05,
                rotateX: 8,
                rotateY: 8,
                boxShadow: '0 40px 100px rgba(0, 255, 255, 0.5), 0 0 120px rgba(0, 128, 255, 0.4)'
              }}
            >
              {/* Project Image/Preview */}
              <div className={`relative h-48 bg-gradient-to-br ${project.color} flex items-center justify-center overflow-hidden`}>
                <motion.div
                  className="text-6xl"
                  whileHover={{ scale: 1.2, rotate: 10 }}
                  transition={{ duration: 0.3 }}
                >
                  {project.image}
                </motion.div>

                {/* Overlay on hover */}
                <motion.div
                  className="absolute inset-0 bg-black/70 flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-300"
                  initial={{ opacity: 0 }}
                  whileHover={{ opacity: 1 }}
                >
                  <div className="flex space-x-4">
                    <motion.a
                      href={project.github}
                      className="px-4 py-2 gaming-btn rounded-lg font-medium"
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      GitHub
                    </motion.a>
                    <motion.a
                      href={project.demo}
                      className="px-4 py-2 gaming-btn rounded-lg font-medium"
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.95 }}
                    >
                      Live Demo
                    </motion.a>
                  </div>
                </motion.div>
              </div>

              {/* Project Content */}
              <div className="p-6">
                <motion.h3 
                  className="text-3xl font-black text-gaming-gradient hover-glow mb-4"
                  whileHover={{ x: 8 }}
                >
                  {project.title}
                </motion.h3>

                <p className="text-gray-300 mb-4 leading-relaxed hover-glow">
                  {project.description}
                </p>

                {/* Problem ‚Üí Solution */}
                <div className="space-y-3 mb-4">
                  <div className="flex items-start space-x-3">
                    <span className="text-red-400 font-semibold text-sm">Problem:</span>
                    <span className="text-gray-400 text-sm hover-glow">{project.problem}</span>
                  </div>
                  <div className="flex items-start space-x-3">
                    <span className="text-green-400 font-semibold text-sm">Solution:</span>
                    <span className="text-gray-400 text-sm hover-glow">{project.solution}</span>
                  </div>
                </div>

                {/* Tech Stack */}
                <div className="flex flex-wrap gap-2 mb-4">
                  {project.techStack.map((tech, techIndex) => (
                    <motion.span
                      key={tech}
                      className="px-3 py-1 text-sm border rounded-full bg-cyan-500/20 text-cyan-300 border-cyan-500/30 hover-glow"
                      initial={{ opacity: 0, scale: 0.8 }}
                      whileInView={{ opacity: 1, scale: 1 }}
                      transition={{ delay: index * 0.1 + techIndex * 0.05, duration: 0.3 }}
                      viewport={{ once: true }}
                      whileHover={{ scale: 1.1, backgroundColor: "rgba(0, 255, 255, 0.3)" }}
                    >
                      {tech}
                    </motion.span>
                  ))}
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-4">
                  <motion.button
                    className="flex-1 py-2 gaming-btn rounded-lg font-medium"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    View Details
                  </motion.button>
                  <motion.button
                    className="px-4 py-2 border border-cyan-500/50 text-cyan-300 rounded-lg hover:border-cyan-400 hover:text-cyan-200 transition-colors hover-glow"
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    ‚≠ê
                  </motion.button>
                </div>
              </div>

              {/* Glow effect */}
              <motion.div
                className={`absolute inset-0 bg-gradient-to-r ${project.color} opacity-0 group-hover:opacity-5 transition-opacity duration-500 pointer-events-none`}
              />
            </motion.div>
          ))}
        </div>

        {/* View More Projects Button */}
        <motion.div
          className="text-center mt-12"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.5, duration: 0.6 }}
          viewport={{ once: true }}
        >
          <motion.button
            className="px-8 py-4 gaming-btn rounded-lg font-semibold text-lg"
            whileHover={{ 
              scale: 1.05,
              boxShadow: "0 10px 30px rgba(0, 255, 255, 0.3)"
            }}
            whileTap={{ scale: 0.95 }}
          >
            View All Projects ‚Üí
          </motion.button>
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
